mapper.select:
select
id, time, money, score, driver_id, customer_id, status, duration
from bill


        and id = #{id}


        and time = #{time}


        and money = #{money}


        and score = #{score}


        and driver_id = #{driverId}


        and customer_id = #{customerId}


        and status = #{status}


        and duration = #{duration}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from bill


        and id = #{id}


        and time = #{time}


        and money = #{money}


        and score = #{score}


        and driver_id = #{driverId}


        and customer_id = #{customerId}


        and status = #{status}


        and duration = #{duration}
