mapper.select:
select
id, start_using_time, band, max_speed, car_type
from car


        and id = #{id}


        and start_using_time = #{startUsingTime}


        and band = #{band}


        and max_speed = #{maxSpeed}


        and car_type = #{carType}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from car


        and id = #{id}


        and start_using_time = #{startUsingTime}


        and band = #{band}


        and max_speed = #{maxSpeed}


        and car_type = #{carType}
