mapper.select:
select
id, time, driver_id, customer_id, bill_id, type, status, contents
from dispute


        and id = #{id}


        and time = #{time}


        and driver_id = #{driverId}


        and customer_id = #{customerId}


        and bill_id = #{billId}


        and type = #{type}


        and status = #{status}


        and contents = #{contents}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from dispute


        and id = #{id}


        and time = #{time}


        and driver_id = #{driverId}


        and customer_id = #{customerId}


        and bill_id = #{billId}


        and type = #{type}


        and status = #{status}


        and contents = #{contents}
