mapper.select:
select
id, name, tel, mail, run_times, password_sha256, prefer_place, score, car_id
from driver


        and id = #{id}


        and name = #{name}


        and tel = #{tel}


        and mail = #{mail}


        and run_times = #{runTimes}


        and password_sha256 = #{passwordSha256}


        and prefer_place = #{preferPlace}


        and score = #{score}


        and car_id = #{carId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from driver


        and id = #{id}


        and name = #{name}


        and tel = #{tel}


        and mail = #{mail}


        and run_times = #{runTimes}


        and password_sha256 = #{passwordSha256}


        and prefer_place = #{preferPlace}


        and score = #{score}


        and car_id = #{carId}
